$('a').on('click', function(){
    let href = $(this).attr('href');

    $('html, body').animate({
        scrollTop:$(href).offset().top
    }, {
        duration: 400,
        easing: "linear"
    });
    return false;

});

$('.main__menu').on('click', function (){
    $('.main__burger, .close').toggleClass('active');
});

$('.close').on('click', function(){
    $('.main__burger').removeClass('active'); 
    $('.close').removeClass('active');
});
